import axios from "axios";
const TUITS_URL = 'http://localhost:4000/api/search';
console.log("TUITS_URL", TUITS_URL);

export const findAllBooks = async () => {
  alert("find book")
  const response = await axios.get(TUITS_URL);
  const books = response.data;
  console.log()
  return books;
};

export const createBook = async (book) => {
  const response = await axios.post(TUITS_URL, book);
  const bookCreated = response.data;
  return bookCreated;
};

export const deleteBook = async (tuitId) => {
  const response = await axios.delete(`${TUITS_URL}/${tuitId}`);
  const status = response.data;
  return status;
};

export const updateBook = async (book) => {
  const response = await axios.put(`${TUITS_URL}/${book._id}`, book);
  const status = response.data;
  return status;
};

export const findAllBooksWithQuerry = async (newtuit) => {
  alert("find book with new querry")
  alert(newtuit.tuit)
  const response = await axios.get(TUITS_URL+'?querry='+newtuit.tuit);
  const books = response.data;
  console.log('data from Querry')
  console.log(books)
  return books;
};
