import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import "../profile/profile.css";
import "./index.css";
import { updateProfile } from "../profile/profile-reducer";


const EditProfile = () => {
    const dispatch = useDispatch();
    const {currentprofile} = useSelector(state => state.profile);
    const [profile, setProfile] = useState(currentprofile);
    const oldprofile = {...currentprofile}
    
    const updateProfileHandler = () =>{
      dispatch(updateProfile(profile)); 
  }
    return(
        <div className="row">
            <div className="col-12 position-relative">
                <ul className="list-group">
                    <li className="list-group-item">
                        <i className="bi bi-x-lg"></i>
                        <label className="EditProfile-label">Edit Profile</label>
                        <button className="rounded-pill btn-dark float-end editprofile-button" onClick={() => 
                        updateProfileHandler(profile)}>Save</button>
                    </li>
                    <li className="list-group-item">
                        <div className="position-relative mb-2">
                            <img src={`/images/${profile.bannerPicture}`} alt="skimag" className="w-100"/>
                            <img src={`/images/${profile.profilePicture}`} alt="skimag" className="profile-img position-absolute wd-nudge-up"/>
                        </div>
                        <div className="tuit-blank">

                        </div>
                   
                        <div className="input-group1 form-horizontal border border-secondary rounded-2 m-2">
                            <div>
                                <label for="profile-name" className="form-label px-3 mb-0 text-secondary">Name:</label>
                            </div>
                            
                            <div class="input-group">
                                <input className="form-control  ml-0 pt-0 border-0 w-25" id="profile-name" value={profile.firstName} 
                                onChange={(event) =>{const newProfile = {...profile, firstName:event.target.value}
                                setProfile(newProfile)}}
                                />
                                <input className="form-control  ml-0 pt-0 border-0 w-75"  value={profile.lastName} 
                                onChange={(event) =>{const newProfile = {...profile, lastName:event.target.value}
                                setProfile(newProfile)}}
                                />
                            </div>
                        </div>
                    
                        <div className="input-group1 form-horizontal border border-secondary rounded-2 m-2">
                            <div>
                                <label for="profile-bio" className="form-label px-3 mb-0 text-secondary">Bio:</label>
                            </div>
                            <div>
                                <textarea class="form-control border-0" id="profile-bio" value={profile.bio} rows="3"></textarea>
                            </div>
                        </div>

                        <div className="form-horizontal border border-secondary rounded-2 m-2">
                            <div>
                                <label for="profile-location" className="form-label px-3 mb-0 text-secondary">Location:</label>
                                <input className="form-control  ml-0 pt-0 border-0" id="profile-location" value={profile.location} />
                            </div>

                        </div>

                        <div className="form-horizontal border border-secondary rounded-2 m-2">
                            <div>
                                <input className="form-control  ml-0 pt-0 border-0" id="profile-website" placeholder="Website" />
                            </div>
                        </div>

                        <div>
                            <label for="profile-dateOfBirth" className="form-label px-3 mb-0 text-secondary">Birth Date:</label>
                            <input className="form-control  ml-0 pt-0 border-0" id="profile-dateOfBirth" value={profile.dateOfBirth} />
                        </div>

                    </li>

                </ul>
            </div>
        </div>
    )
}
export default EditProfile;
