import React, {useState,useEffect} from "react";
import BooksScreen from "./books-screen";
import {findBooksWithQuerryThunk} from "./books-thunks";
import {useDispatch} from "react-redux";

const SearchComponent = () => {
  let [searchStr, setSearchStr] = useState('');
  const dispatch = useDispatch();
  const searchClickHandler = () => {
    alert(searchStr)
    const newTuit = {
      tuit: searchStr,
    }
    dispatch(findBooksWithQuerryThunk(newTuit));
  }

 return(
   <>
     <div className="row">
       <div className="col-11 position-relative">
         <input placeholder="Search Tuiter"
                className="form-control rounded-pill ps-5" onChange={(event) => setSearchStr(event.target.value)}/>
         <i className="bi bi-search position-absolute 
                       wd-nudge-up"></i>
       </div>
       <div className="col-1">
         <i className="wd-bottom-4 text-primary float-end bi 
                       bi-gear-fill fs-2 position-relative"></i>
       </div>
     </div>
     <div>
          <button className="rounded-pill btn btn-primary float-end mt-2 ps-3 pe-3 fw-bold"
                 onClick={searchClickHandler}>
            Search
         </button>
     </div>
     <h1> Search Books </h1>
     <BooksScreen/>
   </>
 );
};
export default SearchComponent;
