import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteTuit, addTuit, updateTuit } from "./books-reducer";
import { findAllBooks } from "./books-service";
import {
  findAllBooksThunk,
  createBookThunk,
  updateBookThunk,
  deleteBookThunk,
  findBooksWithQuerryThunk
} from "./books-thunks";

function BooksScreen() {
  const { books} = useSelector((state) => state.bookData);
  
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(findBooksWithQuerryThunk())
  }, []);
  console.log("Screen")
  console.log(books)
  const [newBook, setNewBook] = useState({ text: "New Book" });
  
  const titleClickHandler = (title) => {
    alert("titlw")
    alert(title)
    const newTuit = {
      tuit: title,
    }
    dispatch(findBooksWithQuerryThunk(newTuit));
  }
  return (
    <div>
      <h1>Book</h1>

      <ul className="list-group mt-2">
        {books.map((book) => (
          <li className="list-group-item" key={book._id}>
            <div>
              id : {book._id} title:
              <span className="text-primary ml-2 px-2" onClick={() => titleClickHandler(book.title)}>
                   {book.title}
              </span>
              <span className="text-success ml-2 px-2" onClick={() => titleClickHandler(book.Authors)}>
                Author: {book.Authors}</span> 
            </div>

            <div>
            description: 
              <p className="text-justify">
                {book.tuit}
              </p>
            </div>
            <div>
              publisher: {book.publisher}
            </div>
            <div>
              rating:{book.averageRating}
            </div>
            
         
          </li>
        ))}
      </ul>
    </div>
  );
}
export default BooksScreen;
